import React from 'react';
import { useDispatch } from 'react-redux';
import { removeContact, editContact } from '../../redux/contactsSlice';
import { Item, Button, EditButton } from './ContactItem.styled';

const ContactItem = ({ contact }) => {
  const dispatch = useDispatch();

  const handleRemove = () => {
    dispatch(removeContact(contact.id));
  };

  const handleEdit = () => {
    const newName = prompt('Novo nome:', contact.name);
    const newEmail = prompt('Novo email:', contact.email);
    const newPhone = prompt('Novo telefone:', contact.phone);
    if (newName && newEmail && newPhone) {
      dispatch(editContact({ id: contact.id, name: newName, email: newEmail, phone: newPhone }));
    }
  };

  return (
    <Item>
      <span>{contact.name} - {contact.email} - {contact.phone}</span>
      <div>
        <EditButton onClick={handleEdit}>Editar</EditButton>
        <Button onClick={handleRemove}>Remover</Button>
      </div>
    </Item>
  );
};

export default ContactItem;
